export default function Validations (type, errors, setErrors) {

    if (type.name.length > 30) {
        setErrors({
          ...errors,
          name: "The title must have at most 30 characters",
        });
    }
    if(typeof type.name !== 'string') {
        setErrors({ 
            ...errors,
            name: "the title can only contain letters"
        });
    }
}
