import React, { useState, useEffect } from 'react';
import style from './NavBar.module.css';
import Variables from '../Variables/Variables.module.css'
import { NavLink } from 'react-router-dom';


export default function NavBar () {

    const [isSticky, setIsSticky] = useState(false);

    useEffect(() => {
        window.addEventListener('scroll', handleScroll);
        return () => {
        window.removeEventListener('scroll', handleScroll);
        };
    }, []);

    const handleScroll = () => {
        if (window.scrollY > 0) {
        setIsSticky(true);
        } else {
        setIsSticky(false);
        }
    };

    return (

        <header className={style.header}>
            <div className={style.container}>
                <NavLink to={'/Home'} className={style.logo}>
                    <a className={style.img}>j0ak!</a>
                </NavLink>

                <nav className={style.navbar}>
                    <button class={style.close_btn} aria-label="close menu" data-nav-toggler>
                        <ion-icon name="close-outline" aria-hidden="true"></ion-icon>
                    </button>

                    <NavLink to={'/Home'} className={style.logo}>
                        <a className={style.img}>j0ak!</a>
                    </NavLink>

                    <ul className={style.navbar_list}>
                        <NavLink to={'/Home'} className={style.navbar_item}>
                            <a className={`${style.navbar_link} ${style.hover_underline} ${style.acrive}`}>
                                <div className={style.seaprator}></div>
                            </a>
                        </NavLink>
                    </ul>

                </nav>
            </div>


        </header>

        // <header class={style.header}>
