import React from 'react';
import style from './Recipes.module.css';
import Card from '../Card/Card';
import SearchBar from '../SearchBar/SearchBar';
import { useEffect, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { filterDiets, filterType, getAllRecipes } from '../../redux/actions';


export default function Recipes () {
    const dispatch = useDispatch();
    const recipes = useSelector(state => state.allRecipes);
    const [filtredRecipes, setFiltredRecipes] = useState([]);
    // const [selectedDiets, setSelectedDiets] = useState({});
    // const [filtredDiets, setFiltredDiets] = useState([]);



    const handleOrder = (e) => {    
        if (e.target.value == "A") {
            console.log(filtredRecipes)
            setFiltredRecipes({
                filtredRecipes: filtredRecipes.length 
                    ? filtredRecipes + filtredRecipes.sort((a, b) => {
                        console.log('bien')
                        if (a.name.toLowerCase() < b.name.toLowerCase()) return -1;
                        if (a.name.toLowerCase() > b.name.toLowerCase()) return 1;
                        return 0;
                    })
                    : filtredRecipes + recipes.sort((a, b) => {
                        console.log('mal')
                        if (a.name.toLowerCase() < b.name.toLowerCase()) return -1;
                        if (a.name.toLowerCase() > b.name.toLowerCase()) return 1;
                        return 0;
                    })
            });
        } 
        else if (e.target.value == "D") {
            setFiltredRecipes({
                ...filtredRecipes,
                filtredRecipes: filtredRecipes + recipes.sort((a, b) => {
                    if (a.name.toLowerCase() > b.name.toLowerCase()) return -1;
                    if (a.name.toLowerCase() < b.name.toLowerCase()) return 1;
                    return 0;
                })
            });
        }
        else if (e.target.value == "healthScore+") {
            setFiltredRecipes({
                ...filtredRecipes,
                filtredRecipes: filtredRecipes + recipes.sort((a, b) => b.healthScore - a.healthScore)
            });
        } 
        else if (e.target.value == "healthScore-") {
            setFiltredRecipes({
                ...filtredRecipes,
                filtredRecipes: filtredRecipes + recipes.sort((a, b) => a.healthScore - b.healthScore)
            });
        }
    }

    // const resetButton = ()  => {
    //     setFiltredRecipes([]);
    //     setCurrentPage(1);
    // }

    const [currentPage, setCurrentPage] = useState(1);

    const onSearch = async (Name) => {
        console.log(recipes)
        const response = await recipes.filter((recipe) => recipe.name.toLowerCase().includes(Name.toLowerCase()));
        setFiltredRecipes(response);
        setCurrentPage(1);
    }

    function getCurrentRecipes() {
        const firstIndex = (currentPage - 1) * 9;
        const lastIndex = currentPage * 9;

        return filtredRecipes.length ? filtredRecipes.slice(firstIndex, lastIndex) : recipes.slice(firstIndex, lastIndex);
        }

        function goToPreviousPage() {
            if (currentPage > 1) {
                setCurrentPage(currentPage - 1);
            }
        }

        function goToNextPage() {
            if (currentPage < Math.ceil(filtredRecipes.length ? filtredRecipes.length / 9 : recipes.length / 9)) {
                setCurrentPage(currentPage + 1);
            }
        }

        const handlePageClick = (pageNumber) => {
            setCurrentPage(pageNumber);
        };

        const totalPages = Math.ceil(filtredRecipes.length ? filtredRecipes.length / 9 : recipes.length / 9);

        const renderPageButtons = () => {
        const pageButtons = [];

        const startPage = Math.max(currentPage - 4, 1);
        const endPage = Math.min(currentPage + 4, totalPages);

        for (let i = startPage; i <= endPage; i++) {
        pageButtons.push(
            <button
            key={i}
            onClick={() => handlePageClick(i)}
            className={currentPage === i ? 'active' : ''}
            >
            {i}
            </button>
        );
        }

        if (endPage < totalPages) {
        pageButtons.push(
            <button key="ellipsis">...</button>
        );
        pageButtons.push(
            <button
            key={totalPages}
            onClick={() => handlePageClick(totalPages)}
            >
            {totalPages}
            </button>
        );
        }

        return pageButtons;
    };

    const [selectedDiets, setSelectedDiets] = useState({});
const [filteredRecipes, setFilteredRecipes] = useState([]);

const handleDietsFilter = (event) => {
  const diet = event.target.value;
  const isChecked = event.target.checked;

  setSelectedDiets((prevSelectedDiets) => ({
    ...prevSelectedDiets,
    [diet]: isChecked,
  }));
};

const resetButton = () => {
  setSelectedDiets({});
};

const setDietsFilter = () => {
  const selectedDietKeys = Object.keys(selectedDiets).filter((key) => selectedDiets[key]);

  // If no diets are selected, reset the filtered recipes to the original recipes
  if (selectedDietKeys.length === 0) {
    setFilteredRecipes(recipes);
    return;
  }

  // Filter recipes that contain all selected diets
  const filtered = recipes.filter((recipe) => {
    return selectedDietKeys.every((diet) => {
      return recipe.diets.includes(diet.toLowerCase());
    });
  });

  setFilteredRecipes(filtered);
};


    // const handleDietsFilter = (event) => {
    //     const diet = event.target.value;
    //     const isChecked = event.target.checked;

    //     console.log('Process =====> 1')
    //     if (isChecked) {
    //         return setSelectedDiets((prevSelectedDiets) => ({
    //             ...prevSelectedDiets,
    //             [diet]: isChecked,
    //         }));
    //     }
    //     else {
    //         resetButton();  
    //         return setSelectedDiets((prevSelectedDiets) => ({
    //             ...prevSelectedDiets,
    //             [diet]: isChecked,
    //         }));
    //     }
    // };

    // const setDietsFilter = () => {
    //             console.log('Process =====> 3')
    //             console.log(selectedDiets)
    //     for (const diet in selectedDiets) {
    //             console.log('Process =====> 4', diet)
    //         if (selectedDiets[diet]) {
    //             console.log('Process =====> 5', diet.toLowerCase())     
    //             setFiltredRecipes(recipes.filter((recipe) => recipe.diets.includes(diet.toLowerCase())))
    //         }
    //     }
    // }

    useEffect(() => {
        setDietsFilter();
        console.log(filtredRecipes)
    }, [selectedDiets])    

    useEffect(() => {
        dispatch(getAllRecipes());
        setSelectedDiets({ Vegan: false, Vegetarian: false, "Lacto-Vegetarian": false, Pescetarian: false, "Ovo-Vegetarian": false, Ketogenic: false, "Gluten Free": false, Paleolithic: false, Primal: false, Whole30: false })
    }, [dispatch]);

    return (
        <div className={style.container}>
            <div className={style.containerFilters}>
                <SearchBar onSearch={onSearch} />
                    <select className={style.select1} onChange={handleOrder}>
                        <option className={style.option}>Select Order</option>
                        <option className={style.option} value="A">Ascendent</option>
                        <option className={style.option} value="D">Descending</option>
                        <option className={style.option} value="healthScore+">Healthier</option>
                        <option className={style.option} value="healthScore-">Less Healthy</option>
                    </select>
                    <div className={style.dietsFilter}>
                        <h3 className={style.option}>Select Diet</h3>
                        <label className={style.select2}>Vegan
                            <input className={style.option} type='checkbox' value="Vegan" onChange={handleDietsFilter}/>
                        </label>
                        <label className={style.select2}>Lacto Ovo-Vegetarian
                            <input className={style.option} type='checkbox' value="Lacto Ovo Vegetarian" onChange={handleDietsFilter}/>
                        </label>
                        <label className={style.select2}>Ketogenic
                            <input className={style.option} type='checkbox' value="Ketogenic" onChange={handleDietsFilter}/>
                        </label>
                        <label className={style.select2}>Gluten Free
                            <input className={style.option} type='checkbox' value="Gluten Free" onChange={handleDietsFilter}/>
                        </label>
                        <label className={style.select2}>Dairy Free
                            <input className={style.option} type='checkbox' value="Dairy Free" onChange={handleDietsFilter}/>
                        </label>
                        <label className={style.select2}>Paleolithic
                            <input className={style.option} type='checkbox' value="Paleolithic" onChange={handleDietsFilter}/>
                        </label>
                        <label className={style.select2}>Primal
                            <input className={style.option} type='checkbox' value="Primal" onChange={handleDietsFilter}/>
                        </label>
                        <label className={style.select2}>Whole30
                            <input className={style.option} type='checkbox' value="Whole 30" onChange={handleDietsFilter}/>
                        </label>
                    </div>
                    <select className={style.select1} onChange={handleOrder}>
                        <option className={style.option}>Select Type</option>
                        <option className={style.option} value="DB">Data Base Recipes</option>
                        <option className={style.option} value="API">Api Recipes</option>
                    </select>
                    <button className={style.boton} onClick={resetButton}>Reset Filter</button> 
            </div>
         <div className={style.containerCard}>
            {getCurrentRecipes().map(({id, name, image, summary, healthScore, stepByStep, price, diets}) => {
            return (
                <Card
                    key={id}
                    id={id} 
                    name={name} 
                    summary={summary} 
                    healthScore={healthScore} 
                    stepByStep={stepByStep} 
                    image={image}
                    price={price}
                    diets={diets}
                />
                )
            }) 
         }
         </div>

            <div className="pagination">
                <button
                onClick={goToPreviousPage}
                disabled={currentPage === 1}
                >
                &laquo; Prev
                </button>

                {renderPageButtons()}

                <button
                onClick={goToNextPage}
                disabled={currentPage === totalPages}
                >
                Next &raquo;
                </button>
            </div>
        </div>
    )
}
