import React, { useState, useRef } from 'react';
import style from './NavBar.module.css';

export default function NavBar() {
  const [menuClass, setMenuClass] = useState('');
  const menuIcon = useRef(null);
  const navBar = useRef(null);

  const toggleMenu = () => {
    menuIcon.current.classList.toggle("bx-x");
    navBar.current.classList.toggle("act");
    setMenuClass(navBar.current.className);
  };

  return (
    <header className={style.header}>
      <div className="bx bx-menu" className={style.menu_icon} ref={menuIcon} onClick={toggleMenu}></div>

      <nav className={`${style.navbar} ${menuClass}`} ref={navBar}>
        <a className={style.link}>Home</a>
        <a className={style.link}>Recipes</a>
        <a className={style.link}>Diets</a>
        <a className={style.link}>Favorites</a>
        <a className={style.link}>About</a>
      </nav>
    </header>
  );
}
