import { ORDERING, GET_ID_RECIPES, GET_NAME_RECIPES, GET_ALL_DIETS, GET_ALL_RECIPES } from "./actions";


const initialState = {
    allRecipes: [],
    idRecipe: [],
    nameRecipes: [],
    allDiets: [],
    filtredRecipes: [],
}


const rootReducer = (state = initialState, actions) => {
    switch (actions.type) {
      case GET_ALL_RECIPES:
        return {
          ...state,
          allRecipes: actions.payload,
        };

      case GET_ID_RECIPES:
        return {
          ...state,
          idRecipe: actions.payload,
        };

      case GET_NAME_RECIPES:
        return {
          ...state,
          allRecipes: actions.payload,
        };

      case GET_ALL_DIETS:
        return {
          ...state,
          allDiets: actions.payload,
        };

      case ORDERING:
          if (actions.payload === "A") {
            // setSelectedDiets({ A: !selectedDiets.A })
              return {
                ...state,
                filtredRecipes: state.filtredRecipes.length
                  ? filtredRecipes.sort((a, b) => a.name.localeCompare(b.name))
                  : allRecipes.sort((a, b) => a.name.localeCompare(b.name))
              };
          } 
          else if (actions.payload === "D") {
              // setSelectedDiets({ D: !selectedDiets.D })
              return {
                ...state,
                filtredRecipes: state.filtredRecipes.length
                  ? filtredRecipes.sort((a, b) => b.name.localeCompare(a.name))
                  : allRecipes.sort((a, b) => b.name.localeCompare(a.name))
              };
          }
          else if (actions.payload === "healthScore+") {
              // setSelectedDiets({ hplus: !selectedDiets.hplus })
              return {
                ...state,
                filtredRecipes: state.filtredRecipes.length
                  ? filtredRecipes.sort((a, b) => b.healthScore - a.healthScore)
                  : allRecipes.sort((a, b) => b.healthScore - a.healthScore)
              };
          } 
          else if (actions.payload === "healthScore-") {
              // setSelectedDiets({ hless: !selectedDiets.hless })
              return {
                ...state,
                filtredRecipes: state.filtredRecipes.length
                  ? filtredRecipes.sort((a, b) => a.healthScore - b.healthScore)
                  : allRecipes.sort((a, b) => a.healthScore - b.healthScore)
              };
          }
          else if (actions.payload == "Price+") {
              // setSelectedDiets({ pplus: !selectedDiets.pplus })
              return {
                ...state,
                filtredRecipes: state.filtredRecipes.length
                  ? filtredRecipes.sort((a, b) => b.price - a.price)
                  : allRecipes.sort((a, b) => b.price - a.price),
              };
          } 
          else if (actions.payload == "Price-") {
              // setSelectedDiets({ pless: !selectedDiets.pless })
              return {
                ...state,
                filtredRecipes: state.filtredRecipes.length
                  ? filtredRecipes.sort((a, b) => a.price - b.price)
                  : allRecipes.sort((a, b) => a.price - b.price),
              };
          }

      default:
        return { ...state };
    }
}

export default rootReducer;
