import style from './Card.module.css';
import { NavLink } from 'react-router-dom';
import { addFav, removeFav } from "../../redux/actions";
import { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';

export default function Card({
        id,
        name,
        image,
        summary,
        healthScore,
        stepByStep,
    }) {

   const dispatch = useDispatch();
   const [isFav, setIsFav] = useState(false);

   const { myFavorites } = useSelector((state) => state)

   const handleFavorite = () => {
      if(isFav) {
         setIsFav(false);
         dispatch(removeFav(id));
      }
      else {
         setIsFav(true);
         dispatch(addFav({
            id, 
            name, 
            summary, 
            healthScore, 
            stepByStep,
            image,
         }));
      }
   }

   useEffect(() => {
      myFavorites.forEach((fav) => {
         if (fav.id == id) {
            setIsFav(true);
         }
      });
   }, [myFavorites])

   return (
      <div className={style.container}>
            <figure>
               <img src={image} alt='no cargo' className={style.img}/>
               <figcaption>
                  {isFav ? (
                     <button className={style.fav} onClick={handleFavorite}>‚ù§Ô∏è</button>
                  ) : (
                     <button className={style.fav} onClick={handleFavorite}>ü§ç</button>
                  )}
                  <NavLink className={style.navStyle} to={`/detail/${id}`}>
                     <h1 className={style.h1}>{name}</h1>
                  </NavLink>
                  <h2 className={style.h2}>Summary: {summary}</h2>
                  <h2 className={style.h2}>Health Score: {healthScore}</h2>
                  <h2 className={style.h2}>Step By Step: {stepByStep}</h2>
               </figcaption>
            </figure>
      </div>
   );
}
