import React, { useState, useEffect } from 'react';
import style from './NavBar.module.css';
import Variables from '../Variables/Variables.module.css'
import { NavLink } from 'react-router-dom';


export default function NavBar () {


  const [isNavActive, setIsNavActive] = useState(false);
  const [isHeaderHide, setIsHeaderHide] = useState(false);

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  const handleScroll = () => {
    const isScrollBottom = window.scrollY > lastScrollPos;
    setIsHeaderHide(isScrollBottom);
    lastScrollPos = window.scrollY;
  };

  const toggleNavbar = () => {
    setIsNavActive(!isNavActive);
    document.body.classList.toggle('nav-active');
  };

  const hideHeader = () => {
    if (isHeaderHide) {
      setIsHeaderHide(true);
    } else {
      setIsHeaderHide(false);
    }
  };

  let lastScrollPos = 0;

    return (

         <header className={`${style.header} ${isHeaderHide ? style.hide : ''} ${isNavActive ? style.active : ''}`}>

            <div className={style.container}>

                <NavLink to={'/Home'} className={style.logo}>
                    <a className={style.img}>j0ak!</a>
                </NavLink>

                <nav className={`${style.navbar} ${style.active}`}>
                    <button class={style.close_btn} aria-label="close menu" data-nav-toggler>
                        <ion-icon name="close-outline" aria-hidden="true"></ion-icon>
                    </button>

                    <NavLink to={'/Home'} className={style.logo}>
                        <a className={style.img}>j0ak!</a>
                    </NavLink>

                    <ul className={style.navbar_list}>
                        <NavLink to={'/Home'} className={style.navbar_item}>
                            <a className={`${style.navbar_link} ${style.hover_underline} ${style.active}`}>
                                <div className={style.separator}></div>
                                <span className={style.span}>Home</span>
                            </a>
                        </NavLink>
                        <NavLink to={'/Recipes'} className={style.navbar_item}>
                            <a className={`${style.navbar_link} ${style.hover_underline} ${style.active}`}>
                                <div className={style.separator}></div>
                                <span className={style.span}>Recipes</span>
                            </a>
                        </NavLink>
                        <NavLink to={'/Diets'} className={style.navbar_item}>
                            <a className={`${style.navbar_link} ${style.hover_underline} ${style.active}`}>
                                <div className={style.separator}></div>
                                <span className={style.span}>Diets</span>
                            </a>
                        </NavLink>
                        <NavLink to={'/Favorites'} className={style.navbar_item}>
                            <a className={`${style.navbar_link} ${style.hover_underline} ${style.active}`}>
                                <div className={style.separator}></div>
                                <span className={style.span}>Favorites</span>
                            </a>
                        </NavLink>
                        <NavLink to={'/About'} className={style.navbar_item}>
                            <a className={`${style.navbar_link} ${style.hover_underline} ${style.active}`}>
                                <div className={style.separator}></div>
                                <span className={style.span}>About</span>
                            </a>
                        </NavLink>
                    </ul>

                </nav>

                <NavLink to={'/Form'} className={`${style.btn} ${style.btn_secondary}`}>
                    <span className={`${style.text} ${style.text_1}`}>Create your own!</span>
                    <span className={`${style.text} ${style.text_2}`} aria-hidden='true'>Create your own!</span>
                </NavLink>

                <button className={style.nav_open_btn} aria-label='open menu' data-nav-toggler>
                    <span className={`${style.line} ${style.line_1}`}></span>
                    <span className={`${style.line} ${style.line_2}`}></span>
                    <span className={`${style.line} ${style.line_3}`}></span>
                </button>

                <div className={style.overlay} data-nav-toggler data-overaly></div>

            </div>

        </header>
    )
}
