import React, { useState } from 'react';
import { getNameRecipes } from '../../redux/actions';
import { useDispatch } from 'react-redux';

export default function SearchBar () {
    const dispatch = useDispatch();
    const [name, setName] = useState('');

    const handleChange = (event) => {
        setName(event.target.value);
        console.log(event.target.value)
    }

    const onSearch = (name) => {
        dispatch(getNameRecipes(name))
    }

    return (
        <div className={style.container}>
            <input type='search' className={style.input} value={name} onChange={handleChange}/>
            <button className={style.btn} onClick={navigation}>
                <span className={`${style.text} ${style.text_1}`}>View The Recipes</span>
                <span className={`${style.text} ${style.text_2} `} aria-hidden="true">View The Recipes</span>
            </button>
            <button className={style.btn} onClick={() => onSearch(name)}><i class='bx bx-search-alt'></i></button>
        </div>
    )
}
