import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import style from './NavBar.module.css';
import Variables from '../Variables/Variables.module.css'
import { NavLink } from 'react-router-dom';

export default function NavBar() {
  const [isSticky, setIsSticky] = useState(false);
  const [isMobileNavOpen, setIsMobileNavOpen] = useState(false);
  const location = useLocation();
  const [currentPath, setCurrentPath] = useState(location.pathname);
  const nav = useNavigate();
  const navigation = () => {
    nav('/Form')
  }

  const handleScroll = () => {
    const scrollTop = window.pageYOffset;
    setIsSticky(scrollTop > 10);
  };

  const toggleMobileNav = () => {
    setIsMobileNavOpen(!isMobileNavOpen);
  };

  useEffect(() => {
    setCurrentPath(location.pathname);
  }, [location]);

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <header className={`${style.header} ${isSticky ? style.sticky : ''}`}>
      <a href='#' className={style.logo}>j0aki</a>

      <nav className={`${style.navbar} ${isMobileNavOpen ? style.navbar_open : ''}`}>
        <div className={style.navbar_links}>
          <NavLink to={'/Home'} className={style.link}>
            <a className={currentPath === '/Home' ? style.act : ''}>Home</a>
          </NavLink>
          <NavLink to={'/Recipes'} className={style.link}>
            <a className={currentPath === '/Recipes' ? style.act : ''}>Recipes</a>
          </NavLink>
          <NavLink to={'/Diets'} className={style.link}>
            <a className={currentPath === '/Diets' ? style.act : ''}>Diets</a>
          </NavLink>
          <NavLink to={'/Favorites'} className={style.link}>
            <a className={currentPath === '/Favorites' ? style.act : ''}>Favorites</a>
          </NavLink>
          <NavLink to={'/About'} className={style.link}>
            <a className={currentPath === '/About' ? style.act : ''}>About</a>
          </NavLink>
        </div>

        <button className={style.btn} onClick={navigation}>
          <span className={`${style.text} ${style.text_1}`}>Create Your Own!</span>
          <span className={`${style.text} ${style.text_2} `} aria-hidden="true">Create Your Own!</span>
        </button>
      </nav>

      <div className={`${style.menu_icon} ${isMobileNavOpen ? style.menu_icon_open : ''}`} onClick={toggleMobileNav}>
        <i className='bx bx-menu'></i>
      </div>
    </header>
  );
}
