import React from 'react';
import style from './Recipes.module.css';
import Card from '../Card/Card';
import SearchBar from '../SearchBar/SearchBar';
import { useEffect, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { getAllRecipes } from '../../redux/actions';


export default function Recipes () {
  const dispatch = useDispatch();
  const recipes = useSelector((state) => state.allRecipes);

  useEffect(() => {
    dispatch(getAllRecipes());
  }, [dispatch]);

  const [currentPage, setCurrentPage] = useState(1);
  const [filteredRecipes, setFilteredRecipes] = useState([]);
  const [selectedDiets, setSelectedDiets] = useState({});
  const [sortBy, setSortBy] = useState('');

  const handleSort = (e) => {
    setSortBy(e.target.value);
  };

  useEffect(() => {
    const sortRecipes = (recipes) => {
      switch (sortBy) {
        case 'A':
          return recipes.slice().sort((a, b) => a.name.localeCompare(b.name));
        case 'D':
          return recipes.slice().sort((a, b) => b.name.localeCompare(a.name));
        case 'healthScore+':
          return recipes.slice().sort((a, b) => b.healthScore - a.healthScore);
        case 'healthScore-':
          return recipes.slice().sort((a, b) => a.healthScore - b.healthScore);
        default:
          return recipes;
      }
    };

    const filteredAndSortedRecipes = sortRecipes(
      recipes.filter((recipe) =>
        Object.entries(selectedDiets).every(
          ([_, value]) => !value || recipe.diets.includes(_.toLowerCase())
        )
      )
    );

    setFilteredRecipes(filteredAndSortedRecipes);
    setCurrentPage(1);
  }, [recipes, selectedDiets, sortBy]);

  const handleFilterType = (e) => {
    e.preventDefault();
  };

  const handleFilterDiets = (e) => {
    const diet = e.target.value;
    const isChecked = e.target.checked;

    setSelectedDiets((prevSelectedDiets) => ({
      ...prevSelectedDiets,
      [diet]: isChecked,
    }));
  };

  const handleReset = () => {
    setSelectedDiets({});
    setFilteredRecipes([]);
    setCurrentPage(1);
    setSortBy('');
  };

  const handlePageClick = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  const pageSize = 9;
  const totalPages = Math.ceil(filteredRecipes.length / pageSize);

  const visibleRecipes = filteredRecipes.slice(
    (currentPage - 1) * pageSize,
    currentPage * pageSize
  );

  const pageButtons = [];

  const startPage = Math.max(currentPage - 4, 1);
  const endPage = Math.min(currentPage + 4, totalPages);

  for (let i = startPage; i <= endPage; i++) {
    pageButtons.push(
      <button
        key={i}
        onClick={() => handlePageClick(i)}
        className={currentPage === i ? 'active' : ''}
      >
        {i}
      </button>
    );
  }

  if (endPage < totalPages) {
    pageButtons.push(<button key="ellipsis">...</button>);
    pageButtons.push(
      <button key={totalPages} onClick={() => handlePageClick(totalPages)}>
        {totalPages}
      </button>
    );
  }

    return (
        <div className={style.container}>
            <div className={style.containerFilters}>
                <SearchBar onSearch={onSearch} />
                    <select className={style.select1} onChange={handleOrder}>
                        <option className={style.option}>Select Order</option>
                        <option className={style.option} value="A">Ascendent</option>
                        <option className={style.option} value="D">Descending</option>
                        <option className={style.option} value="healthScore+">Healthier</option>
                        <option className={style.option} value="healthScore-">Less Healthy</option>
                    </select>
                    <div className={style.dietsFilter}>
                        <h3 className={style.option}>Select Diet</h3>
                        <label className={style.select2}>Vegan
                            <input className={style.option} type='checkbox' value="Vegan" onChange={handleFilterDiets} checked={selectedDiets.Vegetarian}/>
                        </label>
                        <label className={style.select2}>Vegetarian
                            <input className={style.option} type='checkbox' value="Vegetarian"/>
                        </label>
                        <label className={style.select2}>Lacto-Vegetarian
                            <input className={style.option} type='checkbox' value="Lacto-Vegetarian"/>
                        </label>
                        <label className={style.select2}>Ovo-Vegetarian
                            <input className={style.option} type='checkbox' value="Ovo-Vegetarian"/>
                        </label>
                        <label className={style.select2}>Ketogenic
                            <input className={style.option} type='checkbox' value="Ketogenic"/>
                        </label>
                        <label className={style.select2}>Gluten Free
                            <input className={style.option} type='checkbox' value="Gluten Free"/>
                        </label>
                        <label className={style.select2}>Pescetarian
                            <input className={style.option} type='checkbox' value="Pescetarian"/>
                        </label>
                        <label className={style.select2}>Paleolithic
                            <input className={style.option} type='checkbox' value="Paleolithic"/>
                        </label>
                        <label className={style.select2}>Primal
                            <input className={style.option} type='checkbox' value="Primal"/>
                        </label>
                        <label className={style.select2}>Whole30
                            <input className={style.option} type='checkbox' value="Whole30"/>
                        </label>
                    </div>
                    <select className={style.select1} onChange={handleOrder}>
                        <option className={style.option}>Select Type</option>
                        <option className={style.option} value="DB">Data Base Recipes</option>
                        <option className={style.option} value="API">Api Recipes</option>
                    </select>
                    <button className={style.boton} onClick={resetButton}>Reset Filter</button> 
            </div>
         <div className={style.containerCard}>
            {getCurrentRecipes().map(({id, name, image, summary, healthScore, stepByStep, price, diets}) => {
            return (
                <Card
                    key={id}
                    id={id} 
                    name={name} 
                    summary={summary} 
                    healthScore={healthScore} 
                    stepByStep={stepByStep} 
                    image={image}
                    price={price}
                    diets={diets}
                />
                )
            }) 
         }
         </div>

            <div className="pagination">
                <button
                onClick={goToPreviousPage}
                disabled={currentPage === 1}
                >
                &laquo; Prev
                </button>

                {renderPageButtons()}

                <button
                onClick={goToNextPage}
                disabled={currentPage === totalPages}
                >
                Next &raquo;
                </button>
            </div>
        </div>
    )
}
