export default function Validations (type, errors, setErrors) {
    const validateURL = (url) => {
        return /^(ftp|http|https):\/\/[^ "]+$/.test(url);
    }

    // Recipe Name Validation:

    if (type.name.value.length > 30) {
        setErrors({
          ...errors,
          name: "The title must have at most 30 characters",
        });
    }
    if (typeof type.name !== 'string') {
        setErrors({ 
            ...errors,
            name: "The title can only contain letters"
        });
    }
    if (type.name.length < 30 && typeof type.name === 'string') {
        setErrors({ 
            ...errors,
            name: ""
        });
    }

    // Recipe Price Validation:

    if (type.price.length > 9) {
        setErrors({
            ...errors,
            price: "The price is too big",
        });
    }
    if (typeof type.price !== 'number') {
        setErrors({ 
            ...errors,
            price: "The price can only contain numbers"
        });
    }
    if (type.price.length < 9 && typeof type.price === 'number') {
        setErrors({ 
            ...errors,
            price: ""
        });
    }

    // Recipe Image Validation:

    if (type.image) {
        const isValid = validateURL(type.image.value)
        if (isValid === false) {
            setErrors({ 
                ...errors,
                image: "The image url its invalid"
            });
        }
        else {
            setErrors({ 
                ...errors,
                image: ""
            });
        }
    }

    // Recipe Health Score Validation:

        if (typeof type.healthScore !== "number") {
          setErrors({
            ...errors,
            healthScore: "The Health Score can only contain numbers",
          });
        }
        if (type.healthScore.value > 100) {
          setErrors({
            ...errors,
            healthScore: "The Health Score is too much",
          });
        }
        if (type.healthScore.value < 100 && typeof type.healthScore === "number") {
          setErrors({
            ...errors,
            healthScore: "",
          });
        }
}
