import { FILTER_DIETS, ORDERING, GET_ID_RECIPES, GET_NAME_RECIPES, GET_ALL_DIETS, GET_ALL_RECIPES } from "./actions";


const initialState = {
    allRecipes: [],
    idRecipe: [],
    nameRecipes: [],
    allDiets: [],
    filtredRecipes: [],
}


const rootReducer = (state = initialState, actions) => {
    switch (actions.type) {
      case GET_ALL_RECIPES:
        return {
          ...state,
          allRecipes: actions.payload,
        };

      case GET_ID_RECIPES:
        return {
          ...state,
          idRecipe: actions.payload,
        };

      case GET_NAME_RECIPES:
        return {
          ...state,
          allRecipes: actions.payload,
        };

      case GET_ALL_DIETS:
        return {
          ...state,
          allDiets: actions.payload,
        };

      case ORDERING:
          if (actions.payload === "A") {
              return {
                ...state,
                filtredRecipes: state.filtredRecipes.length
                  ? state.filtredRecipes.sort((a, b) => a.name.localeCompare(b.name))
                  : state.allRecipes.sort((a, b) => a.name.localeCompare(b.name))
              };
          } 
          else if (actions.payload === "D") {
              return {
                ...state,
                filtredRecipes: state.filtredRecipes.length
                  ? state.filtredRecipes.sort((a, b) => b.name.localeCompare(a.name))
                  : state.allRecipes.sort((a, b) => b.name.localeCompare(a.name))
              };
          }
          else if (actions.payload === "healthScore+") {
              return {
                ...state,
                filtredRecipes: state.filtredRecipes.length
                  ? state.filtredRecipes.sort((a, b) => b.healthScore - a.healthScore)
                  : state.allRecipes.sort((a, b) => b.healthScore - a.healthScore)
              };
          } 
          else if (actions.payload === "healthScore-") {
              return {
                ...state,
                filtredRecipes: state.filtredRecipes.length
                  ? state.filtredRecipes.sort((a, b) => a.healthScore - b.healthScore)
                  : state.allRecipes.sort((a, b) => a.healthScore - b.healthScore)
              };
          }
          else if (actions.payload == "Price+") {
              return {
                ...state,
                filtredRecipes: state.filtredRecipes.length
                  ? state.filtredRecipes.sort((a, b) => b.price - a.price)
                  : state.allRecipes.sort((a, b) => b.price - a.price),
              };
          } 
          else if (actions.payload == "Price-") {
              return {
                ...state,
                filtredRecipes: state.filtredRecipes.length
                  ? state.filtredRecipes.sort((a, b) => a.price - b.price)
                  : state.allRecipes.sort((a, b) => a.price - b.price),
              };
          }

      default:
        return { ...state };
    }
}

export default rootReducer;
