import style from './Detail.module.css'
import { useDispatch, useSelector } from 'react-redux'
import { getIdRecipes } from '../../redux/actions'
import { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom'
import parse from 'html-react-parser';

export default function Detail () {
    const dispatch = useDispatch();
    const { id } = useParams();
    const getRecipe = useSelector(state => state.idRecipe);
    const recipe = getRecipe.length ? getRecipe[0] : {};
    const [summary, setSummary] = useState([]);
    const [showMoreState, setShowMoreState] = useState(true);

    const showMore = () => {
        setShowMoreState(!showMoreState)
    }

    useEffect( () => {
        dispatch(getIdRecipes(id))
    }, [dispatch])

    useEffect( () => {
        const getSummary = async () => {
            const summary = await recipe.summary && parse(recipe.summary);
            setSummary([summary]);
        }
        getSummary();
    }, [recipe.summary])

    console.log(summary)

    return (
        <div className={style.container}>
            <img src={style.image} />
            <h1 className={style.price}>{recipe.price + '$'}</h1>
            <h1 className={style.title}>{recipe.name}</h1>
            <h3 className={style.data}>{summary}</h3>
            <button className={style.btnFilter} onClick={showMore}>
                <span className={`${style.text} ${style.text_1}`}>Show Step By Step</span>
                <span className={`${style.text} ${style.text_2} `} aria-hidden="true">Show Step By Step</span>
            </button>
            <h3 className={showMoreState ? style.desactive : style.active}>{recipe.stepByStep}</h3>
            <h2 className={style.healthData}>{recipe.healthScore}</h2>
        </div>
    )
}
