import { FILTER_DIETS, ORDERING, GET_ID_RECIPES, GET_NAME_RECIPES, GET_ALL_DIETS, GET_ALL_RECIPES, UPDATE_SELECTED_DIETS } from "./actions";


const initialState = {
    allRecipes: [],
    idRecipe: [],
    nameRecipes: [],
    allDiets: [],
    filtredRecipes: [],
    selectedDiets: [],
}


const rootReducer = (state = initialState, actions) => {
    switch (actions.type) {
      case GET_ALL_RECIPES:
        return {
          ...state,
          allRecipes: actions.payload,
        };

      case GET_ID_RECIPES:
        return {
          ...state,
          idRecipe: actions.payload,
        };

      case GET_NAME_RECIPES:
        return {
          ...state,
          allRecipes: actions.payload,
        };

      case GET_ALL_DIETS:
        return {
          ...state,
          allDiets: actions.payload,
        };

      case ORDERING:
        if (actions.payload === "A") {
          return {
            ...state,
            filtredRecipes: state.filtredRecipes.length
              ? state.filtredRecipes.sort((a, b) =>
                  a.name.localeCompare(b.name)
                )
              : state.allRecipes.sort((a, b) => a.name.localeCompare(b.name)),
          };
        } else if (actions.payload === "D") {
          return {
            ...state,
            filtredRecipes: state.filtredRecipes.length
              ? state.filtredRecipes.sort((a, b) =>
                  b.name.localeCompare(a.name)
                )
              : state.allRecipes.sort((a, b) => b.name.localeCompare(a.name)),
          };
        } else if (actions.payload === "healthScore+") {
          return {
            ...state,
            filtredRecipes: state.filtredRecipes.length
              ? state.filtredRecipes.sort(
                  (a, b) => b.healthScore - a.healthScore
                )
              : state.allRecipes.sort((a, b) => b.healthScore - a.healthScore),
          };
        } else if (actions.payload === "healthScore-") {
          return {
            ...state,
            filtredRecipes: state.filtredRecipes.length
              ? state.filtredRecipes.sort(
                  (a, b) => a.healthScore - b.healthScore
                )
              : state.allRecipes.sort((a, b) => a.healthScore - b.healthScore),
          };
        } else if (actions.payload == "Price+") {
          return {
            ...state,
            filtredRecipes: state.filtredRecipes.length
              ? state.filtredRecipes.sort((a, b) => b.price - a.price)
              : state.allRecipes.sort((a, b) => b.price - a.price),
          };
        } else if (actions.payload == "Price-") {
          return {
            ...state,
            filtredRecipes: state.filtredRecipes.length
              ? state.filtredRecipes.sort((a, b) => a.price - b.price)
              : state.allRecipes.sort((a, b) => a.price - b.price),
          };
        }

      case UPDATE_SELECTED_DIETS:
        return {
          ...state,
          selectedDiets: !state.selectedDiets.includes(actions.payload.selectedDiets) && [...state.selectedDiets, actions.payload.selectedDiets],
        };

      case FILTER_DIETS:
        const selectedDietKeys = Object.keys(state.selectedDiets).filter(
          (key) => state.selectedDiets[key]
        );

        console.log('holaa!!!////', state.selectedDiets)

        if (selectedDietKeys.length === 0) {
          return {
            ...state,
            filtredRecipes: state.allRecipes,
          };
        }

        const filtered = state.allRecipes.filter((recipe) => {
          return selectedDietKeys.every((diet) => {
            return recipe.diets.includes(diet.toLowerCase());
          });
        });

        return {
          ...state,
          filtredRecipes: [filtered],
        }

      default:
        return { ...state };
    }
}

export default rootReducer;
