import React, { useState, useEffect } from 'react';
import style from './NavBar.module.css';
import Variables from '../Variables/Variables.module.css'
import { NavLink } from 'react-router-dom';

export default function NavBar () {
    const [isSticky, setIsSticky] = useState(false);

    useEffect(() => {
        const handleScroll = () => {
            const offset = window.scrollY;
            setIsSticky(offset > 100);
        };
        window.addEventListener('scroll', handleScroll);

        // Limpieza de listeners
        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    }, []);

    // const headerStyle = {
    //     position: isSticky ? 'fixed' : 'absolute',
    //     top: 0,
    //     left: 0,
    //     right: 0,
    //     backgroundColor: isSticky ? '#000' : 'transparent',
    //     transition: 'all 0.3s ease-in-out',
    //     zIndex: 1000
    // }

    return (

        <header className={style.header} style={headerStyle}>
            <NavLink to={'/Home'}className={style.logo}>
                <a className={style}>j0aki</a>
            </NavLink>

            <nav className={style.navbar}>
                <NavLink to={'/Home'}className={style.link}>
                    <a className={style.act}>Home</a>
                </NavLink>
                <NavLink to={'/Recipes'}className={style.link}>
                    <a >Recipes</a>
                </NavLink>
                <NavLink to={'/Diets'}className={style.link}>
                    <a >Diets</a>
                </NavLink>
                <NavLink to={'/Favorites'}className={style.link}>
                    <a >Favorites</a>
                </NavLink>
                <NavLink to={'/About'}className={style.link}>
                    <a >About</a>
                </NavLink>
            </nav>

            <div class="bx bx-moon" className={style.darkIcon}></div>

            <div class="bx bx-menu" className={style.menu_icon}></div>
        </header>

    )
}
