import React, { useState, useEffect } from 'react';
import style from './NavBar.module.css';
import Variables from '../Variables/Variables.module.css'
import { NavLink } from 'react-router-dom';


export default function NavBar () {

    const [isSticky, setIsSticky] = useState(false);

    useEffect(() => {
        window.addEventListener('scroll', handleScroll);
        return () => {
        window.removeEventListener('scroll', handleScroll);
        };
    }, []);

    const handleScroll = () => {
        if (window.scrollY > 0) {
        setIsSticky(true);
        } else {
        setIsSticky(false);
        }
    };

    return (

    <div class={style.topbar}>
        <div class={style.container}>

        <address class={style.topbar_item}>
            <div class={style.icon}>
            <ion-icon name="location-outline" aria-hidden="true"></ion-icon>
            </div>

            <span class={style.span}>
            Restaurant St, Delicious City, London 9578, UK
            </span>
        </address>

        <div class={style.separator}></div>

        <div class={`${style.topbar_item} ${style.item_2}`}>
            <div class="icon">
            <ion-icon name="time-outline" aria-hidden="true"></ion-icon>
            </div>

            <span class="span">Daily : 8.00 am to 10.00 pm</span>
        </div>

        <a href="tel:+11234567890" class="topbar-item link">
            <div class="icon">
            <ion-icon name="call-outline" aria-hidden="true"></ion-icon>
            </div>

            <span class="span">+1 123 456 7890</span>
        </a>

        <div class="separator"></div>

        <a href="mailto:booking@restaurant.com" class="topbar-item link">
            <div class="icon">
            <ion-icon name="mail-outline" aria-hidden="true"></ion-icon>
            </div>

            <span class="span">booking@restaurant.com</span>
        </a>

        </div>
    </div>

        // <header class={style.header}>
        //     <NavLink to={'/Home'} className={style.inicio}>
        //         <a href="#" class={style.logo}>j0ak!</a>
        //     </NavLink>

        //     <nav className={style.navbar}>
        //         <NavLink to={'/Home'} className={style.navLink}>
        //             <a className={style.btn}>Home</a>
        //         </NavLink>
        //         <NavLink to={'/Recipes'} className={style.navLink}>
        //             <a className={style.btn}>Recipes</a>
        //         </NavLink>
        //         <NavLink to={'/Diets'} className={style.navLink}>
        //             <a className={style.btn}>Diets</a>
        //         </NavLink>
        //         <NavLink to={'/Favorites'} className={style.navLink}>
        //             <a className={style.btn}>Favorites</a>
        //         </NavLink>
        //         <NavLink to={'/About'} className={style.navLink}>
        //             <a className={style.btn}>About</a>
        //         </NavLink>
        //     </nav>

        //     {/* <div class="bx bx-menu" id="menu-icon"></div> */}
        // </header>
    )
}
