export default function validationname (type, errors, setErrors) {
  const validateName = /^[A-Za-z]+$/;

  // Recipe Name Validation:

  if (type.name.length) {
    console.log('entro name')

    if (type.name.length > 30) {
      console.log(typeof type.name === "string");
      setErrors({
        ...errors,
        name: "* The title must have at most 30 characters",
      });
    } else if (validateName.test(type.name) === false) {
      setErrors({
        ...errors,
        name: "* The title can only contain letters",
      });
    } else {
      setErrors({
        ...errors,
        name: "",
      });
    }
  }
}
